mapper.select:
select
worksId
from agree
where userId = #{0}
limit #{1},#{2}

select

from agree
where userId = #{0} and worksId = #{1}

select

from agree left join works on agree.worksId = works.id
where works.valid = 1  and agree.userId = #{0}
order by agree.orderTime DESC, works.storyTitle ASC
limit #{1},#{2}
