mapper.select:
select distinct
worksId
from play_list_relation
where playListId = #{0} and userId=#{1}
limit #{2},#{3}

select

from play_list_relation left join works on play_list_relation.worksId = works.id
where works.valid = 1 and play_list_relation.playListId = #{0} and play_list_relation.userId = #{1}
order by play_list_relation.orderTime DESC, works.storyTitle ASC
limit #{2},#{3}
